let xhr=new XMLHttpRequest;xhr.onload=function(){let e=JSON.parse(xhr.responseText);for(let t=0;t<e.length;t++){let n=e[t];if(n.resfield!=""){let e=document.querySelector("[data-rf='"+n.resfield+"']");let t=document.createElement("li");t.className="tv";t.style.fontSize="0.9375rem";let r=document.createTextNode(n.title+", ");let a=document.createElement("i");a.className="tp";a.appendChild(document.createTextNode(n.journal));let l=document.createElement("strong");l.className="tp";l.appendChild(document.createTextNode(n.year));let o=document.createElement("a");o.setAttribute("target","_blank");o.setAttribute("title","Go to paper");o.href="https://doi.org/"+n.doi;o.innerHTML='<svg class="suprachem-icon"><use xlink:href="icons.svg#elink"></use></svg>';t.appendChild(r);t.appendChild(a);t.appendChild(l);t.appendChild(o);e.appendChild(t)}}};const formatDate=e=>{const t=e.getFullYear();const n=String(e.getMonth()+1).padStart(2,"0");const r=String(e.getDate()).padStart(2,"0");return`${t}${n}${r}`};const currentDate=new Date;xhr.open("GET","publications.json?_"+formatDate(currentDate),true);xhr.send();